import React, { useState } from "react";
import "./App.css";

import AddTodo from "./layouts/AddTodo";
import FilterPanel from "./layouts/FilterPanel";
import Todos from "./layouts/Todos";

const App = () => {
  const [data, setData] = useState([]);
  const [todos, setTodos] = useState([]);

  const createTodo = (todo) => {
    setTodos([...todos, todo]);
    setData(todos);
  };

  const clickRemoveTodo = (id) => {
    const result = todos.filter((todo) => {
      return todo.id !== id;
    });
    setTodos(result);
    setData(result);
  };

  const clickChangeComplited = (id) => {
    const result = todos.map((todo) => {
      if (todo.id === id) {
        return { ...todo, complited: !todo.complited };
      } else {
        return todo;
      }
    });
    setTodos(result);
    setData(result);
  };

  const showFilterTodos = (type) => {
    const result = data.filter((todo) => {
      if (type === "Все") {
        return todo;
      } else if (type === "Выполненные") {
        return todo.complited;
      } else if (type === "Невыполненные") {
        return !todo.complited;
      }
    });
    setTodos(result);
  };

  return (
    <div className="container">
      <h1>Todo List</h1>
      <div className="content_todo">
        <AddTodo createTodo={createTodo} />
        <FilterPanel showFilterTodos={showFilterTodos} />
        <Todos
          todos={todos}
          clickRemoveTodo={clickRemoveTodo}
          clickChangeComplited={clickChangeComplited}
        />
      </div>
    </div>
  );
};

export default App;
