import React, {useState} from "react";
import './App.css';

import AddTodo from "./layouts/AddTodo";
import FilterPanel from "./layouts/FilterPanel";
import Todos from "./layouts/Todos";

const App = () => {

  const [todos, setTodos] = useState([

  ])

  const createTodo = (todo) => {
       setTodos([...todos, todo]);
  }

  const clickRemoveTodo = (id) =>{
    const result = todos.filter ((todo) =>{
      return todo.id ===! id

    })
        setTodos(result)
}

  return (
    <div className="container">
      <h1>Todo List</h1>
      <div className="content_todo">
      <AddTodo createTodo={createTodo}/>
      <FilterPanel />
      <Todos todos = {todos} clickRemoveTodo={clickRemoveTodo}/>
      </div>
    </div>
  )
}

export default App;
