import React from "react";
import "../App.css";

import Button from "../components/Button";

const FilterPanel = (props) => {
  const showFilterTodos = (event) => {
    props.showFilterTodos(event.target.innerHTML);
  };

  return (
    <div className="filter_panel">
      <Button text="Все" className="filter_btn all" click={showFilterTodos} />
      <Button
        text="Выполненные"
        className="filter_btn done"
        click={showFilterTodos}
      />
      <Button
        text="Невыполненные"
        className="filter_btn notDone"
        click={showFilterTodos}
      />
    </div>
  );
};

export default FilterPanel;
